import React from "react";
import styles from "./Track.module.css"; // Import des styles spécifiques au composant Track

function Track({ track, isRemoval, onAdd, onRemove }) {
  // Fonction pour déterminer quelle action afficher en fonction de la propriété isRemoval
  const renderAction = () => {
    if (isRemoval) {
      // Si la piste doit être retirée, afficher un bouton avec le symbole "-"
      return (
        <button className={styles["Track-action"]} onClick={passTrackToRemove}>
          -
        </button>
      );
    } else {
      // Sinon, afficher un bouton avec le symbole "+"
      return (
        <button className={styles["Track-action"]} onClick={passTrack}>
          +
        </button>
      );
    }
  };

  // Fonction pour ajouter la piste à la playlist
  const passTrack = () => {
    onAdd(track);
  };

  // Fonction pour retirer la piste de la playlist
  const passTrackToRemove = () => {
    onRemove(track);
  };

  // Rendu du composant Track
  return (
    <div className={styles.Track}>
      <div className={styles["Track-information"]}>
        {/* Affichage du nom de la piste, de l'artiste et de l'album */}
        <h3>{track.name}</h3>
        <p>
          {track.artist} | {track.album}
        </p>
      </div>
      {/* Affichage de l'action correspondante */}
      {renderAction()}
    </div>
  );
}

export default Track;
