import React from "react";
import styles from "./Playlist.module.css"; // Import des styles spécifiques au composant Playlist
import Tracklist from "../Tracklist/Tracklist"; // Import du composant Tracklist

function Playlist({ playlistTracks, onRemove, onSave, onNameChange }) {
  // Fonction pour gérer le changement de nom de la playlist
  const handleNameChange = ({ target }) => {
    onNameChange(target.value);
  };

  // Rendu du composant Playlist
  return (
    <div className={styles.Playlist}>
      {/* Champ de saisie du nom de la playlist */}
      <input defaultValue={"New Playlist"} onChange={handleNameChange} />
      {/* Composant Tracklist pour afficher les pistes de la playlist */}
      <Tracklist
        userSearchResults={playlistTracks}
        onRemove={onRemove}
        isRemoval={true}
      />
      {/* Bouton pour sauvegarder la playlist sur Spotify */}
      <button className={styles["Playlist-save"]} onClick={onSave}>
        SAVE TO SPOTIFY
      </button>
    </div>
  );
}

export default Playlist;
